𝐷 ( 𝑎 , 𝑟 ) ≡ { 𝑧 ∈ C ∶ | 𝑧 − 𝑎 | < 𝑟 } , seg ( 𝑎 , 𝑟 ) ≡ { 𝑧 ∈ C ∶ ℑ 𝑧 = ℑ 𝑎 , | 𝑧 − 𝑎 | < 𝑟 } , 𝑐 ( 𝑒 , 𝜃 , 𝑟 ) ≡ { ( 𝑥 , 𝑦 ) ∈ C ∶ | 𝑥 − 𝑒 | < 𝑦 tan 𝜃 , 0 < 𝑦 < 𝑟 } , 𝐶 ( 𝐸 , 𝜃 , 𝑟 ) ≡ ⋃ 𝑒 ∈ 𝐸 𝑐 ( 𝑒 , 𝜃 , 𝑟 ) .
\begin {gather*}D(a,r)\equiv \{z\in \mathbf {C}\colon \abs {z-a}<r\},\\ \seg (a,r)\equiv \{z\in \mathbf {C}\colon \Im z= \Im a,\ \abs {z-a}<r\},\\ c(e,\theta ,r)\equiv \{(x,y)\in \mathbf {C} \colon \abs {x-e}<y\tan \theta ,\ 0<y<r\},\\ C(E,\theta ,r)\equiv \bigcup _{e\in E}c(e,\theta ,r).\end {gather*}
𝐷(𝑎,𝑟)≡{𝑧∈C∶|𝑧−𝑎|<𝑟}, seg(𝑎,𝑟)≡{𝑧∈C∶ℑ𝑧=ℑ𝑎,|𝑧−𝑎|<𝑟}, 𝑐(𝑒,𝜃,𝑟)≡{(𝑥,𝑦)∈C∶|𝑥−𝑒|<𝑦tan𝜃,0<𝑦<𝑟}, 𝐶(𝐸,𝜃,𝑟)≡⋃𝑒∈𝐸𝑐(𝑒,𝜃,𝑟).

𝛾 𝑥 ( 𝑡 ) = ( cos 𝑡 𝑢 + sin 𝑡 𝑥 , 𝑣 ) , 𝛾 𝑦 ( 𝑡 ) = ( 𝑢 , cos 𝑡 𝑣 + sin 𝑡 𝑦 ) , 𝛾 𝑧 ( 𝑡 ) = ( cos 𝑡 𝑢 + 𝛼 𝛽 sin 𝑡 𝑣 , − 𝛽 𝛼 sin 𝑡 𝑢 + cos 𝑡 𝑣 ) .
\begin {align*}\gamma _x(t)&=(\cos tu+\sin tx,v),\\ \gamma _y(t)&=(u,\cos tv+\sin ty),\\ \gamma _z(t)&=\left (\cos tu+\frac \alpha \beta \sin tv, -\frac \beta \alpha \sin tu+\cos tv\right ).\end {align*}
𝛾𝑥(𝑡)=(cos𝑡𝑢+sin𝑡𝑥,𝑣), 𝛾𝑦(𝑡)=(𝑢,cos𝑡𝑣+sin𝑡𝑦), 𝛾𝑧(𝑡)=(cos𝑡𝑢+𝛼𝛽sin𝑡𝑣,−𝛽𝛼sin𝑡𝑢+cos𝑡𝑣).

(82) 𝑉 𝑖 = 𝑣 𝑖 − 𝑞 𝑖 𝑣 𝑗 , 𝑋 𝑖 = 𝑥 𝑖 − 𝑞 𝑖 𝑥 𝑗 , 𝑈 𝑖 = 𝑢 𝑖 , for 𝑖 ≠ 𝑗 ; (83) 𝑉 𝑗 = 𝑣 𝑗 , 𝑋 𝑗 = 𝑥 𝑗 , 𝑈 𝑗 𝑢 𝑗 + ∑ 𝑖 ≠ 𝑗 𝑞 𝑖 𝑢 𝑖 .
\begin {alignat}{3} V_i & =v_i - q_i v_j, & \qquad X_i & = x_i - q_i x_j, & \qquad U_i & = u_i, \qquad \text {for $i\ne j$;}\label {eq:B}\\ V_j & = v_j, & \qquad X_j & = x_j, & \qquad U_j & u_j + \sum _{i\ne j} q_i u_i.\end {alignat}
𝑉𝑖=𝑣𝑖−𝑞𝑖𝑣𝑗, 𝑋𝑖=𝑥𝑖−𝑞𝑖𝑥𝑗, 𝑈𝑖=𝑢𝑖,for 𝑖≠𝑗;
(82)
𝑉𝑗=𝑣𝑗, 𝑋𝑗=𝑥𝑗, 𝑈𝑗𝑢𝑗+∑𝑖≠𝑗𝑞𝑖𝑢𝑖.
(83)

𝑉 𝑖 = 𝑣 𝑖 − 𝑞 𝑖 𝑣 𝑗 , 𝑋 𝑖 = 𝑥 𝑖 − 𝑞 𝑖 𝑥 𝑗 , 𝑈 𝑖 = 𝑢 𝑖 , for 𝑖 ≠ 𝑗 ; 𝑉 𝑗 = 𝑣 𝑗 , 𝑋 𝑗 = 𝑥 𝑗 , 𝑈 𝑗 𝑢 𝑗 + ∑ 𝑖 ≠ 𝑗 𝑞 𝑖 𝑢 𝑖 .
\begin {alignat*}3 V_i & =v_i - q_i v_j, & \qquad X_i & = x_i - q_i x_j, & \qquad U_i & = u_i, \qquad \text {for $i\ne j$;} \\ V_j & = v_j, & \qquad X_j & = x_j, & \qquad U_j & u_j + \sum _{i\ne j} q_i u_i.\end {alignat*}
𝑉𝑖=𝑣𝑖−𝑞𝑖𝑣𝑗, 𝑋𝑖=𝑥𝑖−𝑞𝑖𝑥𝑗, 𝑈𝑖=𝑢𝑖,for 𝑖≠𝑗; 𝑉𝑗=𝑣𝑗, 𝑋𝑗=𝑥𝑗, 𝑈𝑗𝑢𝑗+∑𝑖≠𝑗𝑞𝑖𝑢𝑖.
